import React, { useEffect, useState } from "react";
import { Layout } from "../layout"; // Import the Layout component
import { getAllNotices } from "./FetchApi"; // Import your API function

export const Noticeshop = () => {
    const [notices, setNotices] = useState([]); // State to hold notices
    const [loading, setLoading] = useState(true); // Loading state

    // Fetch notices when the component mounts
    useEffect(() => {
        const fetchNotices = async () => {
            try {
                let response = await getAllNotices();
                console.log("API Response:", response); // Log the full response
                if (response.notices) { // Check if the notices array exists
                    setNotices(response.notices); // Store the fetched notices
                } else {
                    console.log("No notices found in the response.");
                }
            } catch (error) {
                console.error("Error fetching notices:", error);
            } finally {
                setLoading(false); // Stop loading after fetching
            }
        };

        fetchNotices();
    }, []); // Empty dependency array ensures it runs once when the component is mounted

    // Loading state display
    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <Layout>
            <div className="notices-container">
                <h1>All Notices</h1>
                <div className="notices-grid">
                    {notices.length > 0 ? (
                        notices.map((notice) => (
                            <div key={notice.nId} className="notice-card">
                                <h2 className="notice-title">{notice.title}</h2>
                                <p className="notice-description">{notice.description}</p>
                                <p className="notice-time">{notice.time}</p>
                            </div>
                        ))
                    ) : (
                        <p>No notices available.</p>
                    )}
                </div>
            </div>
        </Layout>
    );
};

// CSS Styles (You can put this in an external CSS file or use a style tag)
const styles = `
    /* Container for all notices */
    .notices-container {
        padding: 20px;
        background-color: #f4f4f4;
        min-height: 100vh;
    }

    /* Grid layout for notices */
    .notices-grid {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
        gap: 20px;
    }

    /* Each individual notice card */
    .notice-card {
        background-color: white;
        padding: 20px;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        transition: transform 0.3s ease-in-out;
    }

    /* Hover effect for notice cards */
    .notice-card:hover {
        transform: translateY(-5px);
        box-shadow: 0 6px 12px rgba(0, 0, 0, 0.2);
    }

    /* Title styling */
    .notice-title {
        font-size: 1.5rem;
        font-weight: bold;
        color: #333;
    }

    /* Description styling */
    .notice-description {
        font-size: 1rem;
        color: #555;
        margin-top: 10px;
    }

    /* Time styling */
    .notice-time {
        font-size: 0.9rem;
        color: #777;
        margin-top: 10px;
        font-style: italic;
    }
`;

// Add styles to the document head
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);
