import { format } from "date-fns"; // Import date formatting library
import React, { useEffect, useState } from "react";
import Layout from "../layout"; // Import Layout component
import { getAllNotices } from "./FetchApi"; // Import API function

export const Noticeshop = () => {
    const [notices, setNotices] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchNotices = async () => {
            try {
                let response = await getAllNotices();
                if (response.notices) {
                    setNotices(response.notices);
                }
            } catch (error) {
                console.error("Error fetching notices:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchNotices();
    }, []);

    // Function to format time into a more readable format
    const formatTime = (time) => {
        if (!time) return "Unknown Date"; // Handle missing time
        try {
            return format(new Date(time), "MMMM dd, yyyy"); // Example: "February 16, 2025"
        } catch (error) {
            return "Invalid Date"; // If there's an issue with formatting
        }
    };

    if (loading) {
        return <div className="text-center text-gray-600 mt-5">Loading...</div>;
    }

    return (
        <Layout>
            <div className="max-w-3xl mx-auto p-4">
                <h1 className="text-2xl font-bold mb-4">All Notices</h1>
                <div className="space-y-4">
                    {notices.length > 0 ? (
                        notices.map((notice) => (
                            <div
                                key={notice.nId}
                                className="bg-white shadow-md rounded-lg p-4 border border-gray-200"
                            >
                                {/* Notice Header */}
                                <div className="flex justify-between items-center">
                                    <h2 className="text-lg font-semibold">{notice.title}</h2>
                                    <span className="bg-red-500 text-white text-xs px-2 py-1 rounded-full">
                                        New
                                    </span>
                                </div>

                                {/* Author and Date */}
                                <p className="text-sm text-gray-500 mt-2">
                                    {notice.author} â€¢ {formatTime(notice.time)}
                                </p>

                                {/* Notice Description */}
                                <p className="text-gray-700 mt-2">{notice.description}</p>

                                {/* Action Buttons */}
                                <div className="flex justify-between items-center mt-4">
                                    <button className="bg-gray-800 text-white px-3 py-1 rounded hover:bg-gray-700">
                                        Mark as Read
                                    </button>
                                    <button className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-500">
                                        Read More
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="text-gray-500">No notices available.</p>
                    )}
                </div>
            </div>
        </Layout>
    );
};
