import React, { useEffect, useState } from "react";
import Layout from "../layout"; // Import Layout component
import { getAllNotices } from "./FetchApi"; // Import API function

export const Noticeshop = () => {
    const [notices, setNotices] = useState([]);
    const [loading, setLoading] = useState(true);
    const [readNotices, setReadNotices] = useState(new Set()); // Track read notices

    useEffect(() => {
        const fetchNotices = async () => {
            try {
                let response = await getAllNotices();
                if (response.notices) {
                    setNotices(response.notices);
                }
            } catch (error) {
                console.error("Error fetching notices:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchNotices();
    }, []);

    // Function to format the time
    const formatTime = (time) => {
        if (!time) return "Unknown Time";
        const date = new Date(time);
        return date.toLocaleString("en-US", {
            year: "numeric",
            month: "long",
            day: "numeric",
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit",
            hour12: true,
        });
    };

    // Function to mark notice as read
    const markAsRead = (nId) => {
        setReadNotices((prevReadNotices) => new Set(prevReadNotices).add(nId));
    };

    if (loading) {
        return <div className="text-center text-gray-600 mt-5">Loading...</div>;
    }

    return (
        <Layout>
            <div className="max-w-6xl mx-auto p-4 mt-10">
                <h1 className="text-2xl font-bold mb-6 text-center">All Notices</h1>

                {/* Grid Layout - 3 notices per row */}
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
                    {notices.length > 0 ? (
                        notices.map((notice) => (
                            <div
                                key={notice.nId}
                                className="bg-white shadow-md rounded-lg p-4 border border-gray-200"
                            >
                                {/* Notice Header */}
                                <div className="flex justify-between items-center">
                                    <h2 className="text-lg font-semibold">{notice.title}</h2>
                                    {!readNotices.has(notice.nId) && ( // Show "New" badge only if not read
                                        <span className="bg-red-500 text-white text-xs px-2 py-1 rounded-full">
                                            New
                                        </span>
                                    )}
                                </div>

                                {/* Author and Date */}
                                <p className="text-sm text-gray-500 mt-2">
                                    {notice.author} â€¢ {formatTime(notice.time)}
                                </p>

                                {/* Notice Description */}
                                <p className="text-gray-700 mt-2">{notice.description}</p>

                                {/* Action Buttons */}
                                <div className="flex justify-between items-center mt-4">
                                    <button
                                        className="bg-gray-800 text-white px-3 py-1 rounded hover:bg-gray-700"
                                        onClick={() => markAsRead(notice.nId)}
                                    >
                                        Mark as Read
                                    </button>
                                    <button className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-500">
                                        Read More
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="text-gray-500 col-span-3 text-center">No notices available.</p>
                    )}
                </div>
            </div>
        </Layout>
    );
};
