import React, { useEffect, useState } from "react";
import Layout from "../layout"; // Import Layout component
import { getAllNotices } from "./FetchApi"; // Import your API function

export const Noticeshop = () => {
    const [notices, setNotices] = useState([]); // State to hold notices
    const [loading, setLoading] = useState(true); // Loading state

    // Fetch notices when the component mounts
    useEffect(() => {
        const fetchNotices = async () => {
            try {
                let response = await getAllNotices();
                console.log("API Response:", response); // Log the full response
                if (response.notices) { // Check if the notices array exists
                    setNotices(response.notices); // Store the fetched notices
                } else {
                    console.log("No notices found in the response.");
                }
            } catch (error) {
                console.error("Error fetching notices:", error);
            } finally {
                setLoading(false); // Stop loading after fetching
            }
        };

        fetchNotices();
    }, []); // Empty dependency array ensures it runs once when the component is mounted

    // Loading state display
    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <Layout>
            <div>
                <h1>All Notices</h1>
                <div>
                    {notices.length > 0 ? (
                        notices.map((notice) => (
                            <div key={notice.nId} className="notice-card">
                                <h2>{notice.title}</h2>
                                <p>{notice.description}</p>
                                <p>{notice.time}</p>
                            </div>
                        ))
                    ) : (
                        <p>No notices available.</p>
                    )}
                </div>
            </div>
        </Layout>
    );
};
