import React, { useEffect, useState } from "react";
import Layout from "../layout"; // Import Layout component
import { getAllNotices } from "./FetchApi"; // Import your API function

const Noticeshop = () => {
    const [notices, setNotices] = useState([]); // State to hold notices
    const [loading, setLoading] = useState(true); // Loading state

    useEffect(() => {
        const fetchNotices = async () => {
            try {
                let response = await getAllNotices();
                console.log("API Response:", response); // Log the full response
                if (response.notices) { // Check if the notices array exists
                    setNotices(response.notices); // Store the fetched notices
                } else {
                    console.log("No notices found in the response.");
                }
            } catch (error) {
                console.error("Error fetching notices:", error);
            } finally {
                setLoading(false); // Stop loading after fetching
            }
        };

        fetchNotices();
    }, []); // Empty dependency array ensures it runs once when the component is mounted

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    return (
        <Layout>
            <div className="noticeshop-container">
                <h1 className="noticeshop-title">All Notices</h1>
                <div className="notices-list">
                    {notices.length > 0 ? (
                        notices.map((notice) => (
                            <div key={notice.nId} className="notice-card">
                                <h2 className="notice-title">{notice.title}</h2>
                                <p className="notice-description">{notice.description}</p>
                                <p className="notice-time">{notice.time}</p>
                            </div>
                        ))
                    ) : (
                        <p>No notices available.</p>
                    )}
                </div>
            </div>
        </Layout>
    );
};

// CSS for the component (inline or in a separate CSS file)
const styles = `
    .noticeshop-container {
        padding: 20px;
        max-width: 1200px;
        margin: 0 auto;
    }

    .noticeshop-title {
        font-size: 2rem;
        font-weight: bold;
        text-align: center;
        margin-bottom: 20px;
    }

    .notices-list {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(250px, 1fr)); /* Responsive grid */
        gap: 20px;
    }

    .notice-card {
        background-color: #fff;
        border-radius: 10px;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        padding: 20px;
        transition: transform 0.3s ease;
    }

    .notice-card:hover {
        transform: translateY(-5px);
    }

    .notice-title {
        font-size: 1.5rem;
        color: #333;
        margin-bottom: 10px;
    }

    .notice-description {
        font-size: 1rem;
        color: #666;
        margin-bottom: 10px;
    }

    .notice-time {
        font-size: 0.9rem;
        color: #999;
    }

    .loading {
        text-align: center;
        font-size: 1.5rem;
        margin-top: 50px;
    }

    @media (max-width: 768px) {
        .noticeshop-container {
            padding: 10px;
        }

        .noticeshop-title {
            font-size: 1.8rem;
        }

        .notice-title {
            font-size: 1.3rem;
        }

        .notice-description {
            font-size: 0.9rem;
        }

        .notice-time {
            font-size: 0.8rem;
        }
    }

    @media (max-width: 480px) {
        .noticeshop-title {
            font-size: 1.6rem;
        }

        .notice-card {
            padding: 15px;
        }

        .notice-title {
            font-size: 1.2rem;
        }

        .notice-description {
            font-size: 0.85rem;
        }

        .notice-time {
            font-size: 0.75rem;
        }
    }
`;

// Create a style element for the CSS
const styleElement = document.createElement('style');
styleElement.textContent = styles;
document.head.appendChild(styleElement);

export default Noticeshop;
